import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function Customers() {
  const [customers, setCustomers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showMultiAddModal, setShowMultiAddModal] = useState(false); // Modal for adding multiple customers
  const [multiCustomerInput, setMultiCustomerInput] = useState(''); // Input value for multiple customers

  const [currentCustomer, setCurrentCustomer] = useState({
    name: '', 
    phone: '', 
    address: '', 
    birthday: '', 
    remarks: ''
  });
  const [isEditing, setIsEditing] = useState(false);
  const [customerToDelete, setCustomerToDelete] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Show Add Modal
  const handleShowAddModal = () => {
    setIsEditing(false);
    setCurrentCustomer({ name: '', phone: '', address: '', birthday: '', remarks: '' });
    setShowModal(true);
  };

  // Show Edit Modal
  const handleShowEditModal = (customer) => {
    setIsEditing(true);
    setCurrentCustomer({ ...customer, birthday: customer.birthday ? customer.birthday.substring(0,10) : '' });
    setShowModal(true);
  };

  // Save Single Customer (Add or Edit)
  const handleSaveCustomer = async () => {
    try {
      if (isEditing) {
        await axios.put(`http://localhost:3001/api/customers/${currentCustomer._id}`, currentCustomer);
      } else {
        await axios.post('http://localhost:3001/api/customers', currentCustomer);
      }
      setShowModal(false);
      fetchCustomers();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete Customer
  const handleDeleteClick = (customer) => {
    setCustomerToDelete(customer);
    setShowDeleteModal(true);
  };

  const handleDeleteCustomer = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/customers/${customerToDelete._id}`);
      setShowDeleteModal(false);
      setCustomerToDelete(null);
      fetchCustomers();
    } catch (err) {
      console.error(err);
    }
  };

  // ===========================
  // ADD MULTIPLE CUSTOMERS
  // ===========================
  const handleAddMultipleCustomers = async () => {
    const lines = multiCustomerInput.trim().split('\n');

    // Expecting format: name,phone,address,birthday,remarks
    try {
      for (const line of lines) {
        const [name, phone, address, birthday, remarks] = line.split(';').map(x => x.trim());
        if (name) { // Only add if name exists
          await axios.post('http://localhost:3001/api/customers', { name, phone, address, birthday, remarks });
        }
      }
      alert('Multiple customers added successfully!');
      setShowMultiAddModal(false);
      setMultiCustomerInput('');
      fetchCustomers();
    } catch (err) {
      console.error(err);
      alert('Error adding multiple customers. Check console for details.');
    }
  };

  // ===========================
  // COMPONENT RENDER
  // ===========================
  return (
    <div style={{ padding: '20px' }}>
      <h2>Customers</h2>
      <Button variant="primary" onClick={handleShowAddModal}>Add Customer</Button>{' '}
      <Button variant="secondary" onClick={() => setShowMultiAddModal(true)}>Add Multiple Customers</Button>

      {/* Customers Table */}
      <table className="table mt-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Birthday</th>
            <th>Containers</th>
            <th>Created At</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map(c => (
            <tr key={c._id}>
              <td>
                <Link to={`/customers/${c._id}/profile`}>{c.name}</Link>
              </td>
              <td>{c.phone}</td>
              <td>{c.address}</td>
              <td>{c.birthday ? new Date(c.birthday).toLocaleDateString() : ''}</td>
              <td>{c.containers ? c.containers.length : 0}</td>
              <td>{c.createdAt ? new Date(c.createdAt).toLocaleDateString() : ''}</td>
              <td>{c.remarks}</td>
              <td>
                <Button variant="warning" size="sm" onClick={() => handleShowEditModal(c)}>Edit</Button>{' '}
                <Button variant="danger" size="sm" onClick={() => handleDeleteClick(c)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Customer' : 'Add Customer'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" value={currentCustomer.name} onChange={(e) => setCurrentCustomer({ ...currentCustomer, name: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control type="text" value={currentCustomer.phone} onChange={(e) => setCurrentCustomer({ ...currentCustomer, phone: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Address</Form.Label>
              <Form.Control type="text" value={currentCustomer.address} onChange={(e) => setCurrentCustomer({ ...currentCustomer, address: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Birthday</Form.Label>
              <Form.Control type="date" value={currentCustomer.birthday} onChange={(e) => setCurrentCustomer({ ...currentCustomer, birthday: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control as="textarea" rows={2} value={currentCustomer.remarks} onChange={(e) => setCurrentCustomer({ ...currentCustomer, remarks: e.target.value })} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSaveCustomer}>Save</Button>
        </Modal.Footer>
      </Modal>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Customer</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete {customerToDelete?.name}?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>Cancel</Button>
          <Button variant="danger" onClick={handleDeleteCustomer}>Delete</Button>
        </Modal.Footer>
      </Modal>

      {/* Add Multiple Customers Modal */}
      <Modal show={showMultiAddModal} onHide={() => setShowMultiAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Multiple Customers</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Enter multiple customers (one per line, semicolon-separated fields: name,phone,address,birthday,remarks)</Form.Label>
              <Form.Control as="textarea" rows={6} value={multiCustomerInput} onChange={(e) => setMultiCustomerInput(e.target.value)} placeholder="Example: Juan Dela Cruz;09171234567;San Juan;1990-01-01;Frequent buyer" />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowMultiAddModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleAddMultipleCustomers}>Add All</Button>
        </Modal.Footer>
      </Modal>

    </div>
  );
}

export default Customers;
