import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form, Alert } from 'react-bootstrap';
import { Typeahead } from 'react-bootstrap-typeahead';
import { FaEdit, FaTrash, FaPlus } from 'react-icons/fa';

function Sales() {
    const [sales, setSales] = useState([]);
    const [customers, setCustomers] = useState([]);
    const [containers, setContainers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [isEditing, setIsEditing] = useState(false);  

    const [editMode, setEditMode] = useState(false);
    const [selectedSalesIds, setSelectedSalesIds] = useState([]);
    const [checkAll, setCheckAll] = useState(false);
    const [showDeleteSelectedModal, setShowDeleteSelectedModal] = useState(false);
    const [deleting, setDeleting] = useState(false);
    const [deleteProgress, setDeleteProgress] = useState(0);
    const [deleteSuccess, setDeleteSuccess] = useState(false);
    const [showSuccessModal, setShowSuccessModal] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');

    const [currentSale, setCurrentSale] = useState({
      date: new Date().toISOString().split('T')[0],
      customerId: '',
      containerId: '',
      type: 'Delivery',
      item: '',
      quantity: 1,
      pricePerUnit: 0,
      totalAmount: 0,
      paymentMethod: 'Cash',
      status: 'Paid',
      remarks: ''
    });

    const [saleToEditId, setSaleToEditId] = useState(null);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [saleToDelete, setSaleToDelete] = useState(null);
    const [showMultiAddModal, setShowMultiAddModal] = useState(false);
    const [multiSalesInput, setMultiSalesInput] = useState('');

    const [importProgress, setImportProgress] = useState(0);
    const [showImportResultModal, setShowImportResultModal] = useState(false);
    const [importResult, setImportResult] = useState({ successCount: 0, failedLine: null, errorMessage: '' });

    const [currentSaleId, setCurrentSaleId] = useState(null);
    const [showAddCustomerModal, setShowAddCustomerModal] = useState(false);
    const [currentCustomer, setCurrentCustomer] = useState({
      name: '',
      nickname: '',
      phone: '',
      address: '',
      landmark: '',
      birthday: '',
      remarks: '',
      containersOutput: containersOutput || []
    });

    


  // ===============================
  // LOAD SALES & CUSTOMERS
  // ===============================
  useEffect(() => {
    fetchSales();
    fetchCustomers();
  }, []);

  const fetchSales = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/sales');
      setSales(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchContainers = async () => {
  try {
    const res = await axios.get('http://localhost:3001/api/inventory');
    setContainers(res.data);
  } catch (err) {
    console.error(err);
  }
};

useEffect(() => {
  fetchSales();
  fetchCustomers();
  fetchContainers(); // load containers
}, []);
  // ===============================
  // HANDLERS
  // ===============================
  const handleShowAddModal = () => {
    setIsEditing(false);
    setCurrentSale({
      name: '',
      date: new Date().toISOString().split('T')[0],
      customerId: '',
      containerId: '',
      type: 'Delivery',
      item: 'Refill (Slim 5gal)',
      quantity: 1,
      pricePerUnit: 0,
      totalAmount: 0,
      paymentMethod: 'Cash',
      status: 'Paid',
      remarks: ''
    });
    setShowModal(true);
  };

  const handleShowEditModal = (sale) => {
    setIsEditing(true);
    setSaleToEditId(sale._id);
    setCurrentSale({
      date: sale.date ? sale.date.substring(0,10) : new Date().toISOString().split('T')[0],
      customerId: sale.customerId?._id || '',
      customerName: sale.customerName || '',
      type: sale.type,
      item: sale.item || '',
      quantity: sale.quantity,
      pricePerUnit: sale.pricePerUnit || 0,
      totalAmount: sale.totalAmount,
      paymentMethod: sale.paymentMethod,
      status: sale.status,
      containerIds: sale.containerIds || [],
      remarks: sale.remarks || ''
    });
    setShowModal(true);
  };

  const handleSaveSale = async () => {
    try {
      const calculatedPricePerUnit = currentSale.totalAmount && currentSale.quantity
        ? currentSale.totalAmount / currentSale.quantity
        : 0;

    const saleToSave = { 
      ...currentSale, 
      pricePerUnit: calculatedPricePerUnit,
      customerId: currentSale.customerId || null,
      customerName: currentSale.customerName || '',
      customerContainerQty: currentSale.customerContainerQty || 0,
    };


      if (isEditing) {
        await axios.put(`http://localhost:3001/api/sales/${saleToEditId}`, saleToSave);
      } else {
        await axios.post('http://localhost:3001/api/sales', saleToSave);
      }

      setShowModal(false);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteClick = (sale) => {
    setSaleToDelete(sale);
    setShowDeleteModal(true);
  };

  const handleDeleteSale = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/sales/${saleToDelete._id}`);
      setShowDeleteModal(false);
      setSaleToDelete(null);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };


const handleMultiSalesImport = async () => {    
  if (!multiSalesInput) {
    alert('Please enter sales records first.');
    return;
  }

  let importing = true; // ðŸ”‘ local synchronous flag to avoid React async delays

  const lines = multiSalesInput.trim().split('\n');
  let successCount = 0;

  try {
      console.log("Starting import...");
for (let i = 0; i < lines.length; i++) {
      if (!importing) {
        console.log('Import stopped by user.');
        break;
      }

      const line = lines[i];
      const [date, customerName, type, item, quantity, totalAmount, paymentMethod, status, containersField, remarks] = line.split(';').map(f => f.trim());

// Process containers field
let customerContainerQty = 0;
let containerIds = [];
let containersOutput = [];

if (containersField) {
  const containersArray = containersField.split(',').map(c => c.trim());
  for (const c of containersArray) {

  if (c.toLowerCase().startsWith('z')) {
    // Scenario #1, #2, #5: own container qty
    const qty = parseInt(c.substring(1));
    if (!isNaN(qty)) customerContainerQty = qty;
    containersOutput.push(`z${qty}`);
  }

  else if (/^[drs]?\d+$/i.test(c)) {
    // Scenario #1, #2, #3, #4: known container IDs with optional prefix
    let code = c.toUpperCase();

    // Determine prefix if missing based on item type
    if (!isNaN(code)) {
      const idNum = parseInt(code).toString().padStart(3, '0');

      if (item.toLowerCase().includes('slim')) {
        code = `S${idNum}`;
        console.log(`Assigned prefix 'S' for Slim item: ${code}`);
      }
      else if (item.toLowerCase().includes('round')) {
        code = `R${idNum}`;
        console.log(`Assigned prefix 'R' for Round item: ${code}`);
      }
      else if (item.toLowerCase().includes('dispenser')) {
        code = `D${idNum}`;
        console.log(`Assigned prefix 'D' for Dispenser item: ${code}`);
      }
      else {
        console.log(`No matching item type found for code: ${code}`);
      }
    }

    // Find container in Inventory by exact name only
    const containerObj = containers.find(cont =>
      cont && cont.name && cont.name.toUpperCase() === code
    );

    if (containerObj) {
      containerIds.push(containerObj._id);
      containersOutput.push(containerObj.name);
      console.log(`Matched container by name: ${containerObj.name}`);
    } else {
      // If not found, just push raw code
      containersOutput.push(code);
      console.log(`No container found for code: ${code}, pushed raw`);
    }
  }

  else if (/^[drs]\?$/i.test(c)) {
    // Scenario #5: unknown containers recorded as '?'
    containersOutput.push(c.toLowerCase());
    console.log(`Unknown container code: ${c}`);
  }

  else {
    // Any other unhandled format
    containersOutput.push(c);
    console.log(`Unhandled container format: ${c}`);
  }

}
}
      // Calculate
      const qty = parseInt(quantity);
      const total = parseFloat(totalAmount);
      const pricePerUnit = qty !== 0 ? total / qty : 0;

      const customer = customers.find(c => c.name.toLowerCase() === customerName.toLowerCase());

      const sale = {
        date: date || new Date().toISOString().split('T')[0],
        customerId: customer ? customer._id : null,
        customerName: customer ? customer.name : customerName,
        type: type || 'Walk-in',
        item: item || '',
        quantity: qty || 1,
        totalAmount: total || 0,
        pricePerUnit: pricePerUnit || 0,
        paymentMethod: paymentMethod || 'Cash',
        status: status || 'Paid',
        customerContainerQty,
        containerIds,
        
        remarks: `${remarks || ''}${customer ? '' : ` | CustomerName: ${customerName}`}`.trim(),
      };

      await axios.post('http://localhost:3001/api/sales', sale);
      
      successCount++;

      // Update progress bar
      setImportProgress(Math.round(((i + 1) / lines.length) * 100));
    }

    // Show success modal if all completed
    if (successCount === lines.length) {
      setImportResult(prev => ({ 
        ...prev, 
        successCount 
    }));

      setShowMultiAddModal(false);
      setShowImportResultModal(true);
    }

    fetchSales();

  } catch (err) {
