};
  // ===============================
  //  Adding Customer From Records
  // ===============================
const handleAddCustomer = async () => {
const lines = multiSalesInput.split('\n').map(line => line.trim()).filter(line => line);

 // Assuming you have `lines` as the split input lines
for (const line of lines) {
  if (!line.trim()) continue; // skip empty lines

  const [date, customerName, type, item, quantity, totalAmount, paymentMethod, status, containersField, remarks] = line.split(';').map(f => f.trim());

  // STEP 1: Initialize container variables
  let customerContainerQty = 0;
  let containerIds = [];
  let containersOutput = [];

  // STEP 2: Process containers field
  if (containersField) {
    const containersArray = containersField.split(',').map(c => c.trim());
    for (const c of containersArray) {

      if (c.toLowerCase().startsWith('z')) {
        // Scenario #1, #2, #5: own container qty
        const qty = parseInt(c.substring(1));
        if (!isNaN(qty)) customerContainerQty = qty;
        containersOutput.push(`z${qty}`);
      }

      else if (/^[drs]?\d+$/i.test(c)) {
        // Scenario #1, #2, #3, #4: known container IDs with optional prefix
        let code = c.toUpperCase();

        // Scenario #3: if it's just numbers without prefix
        if (!isNaN(code)) {
          const idNum = parseInt(code).toString().padStart(3, '0');

          if (item.toLowerCase().includes('slim')) {
            code = `S${idNum}`;
          }
          else if (item.toLowerCase().includes('round')) {
            code = `R${idNum}`;
          }
          else if (item.toLowerCase().includes('disp')) {
            code = `D${idNum}`;
          }
        }

        // Find container by exact name in inventory database
        const containerObj = containers.find(cont =>
          cont.name.toUpperCase() === code
        );

        if (containerObj) {
          containerIds.push(containerObj._id);
          containersOutput.push(containerObj.name);
        } else {
          // If not found, just push raw code
          containersOutput.push(code);
        }
      }

      else if (/^[drs]\?$/i.test(c)) {
        // Scenario #5: unknown containers recorded as '?'
        containersOutput.push(c.toLowerCase());
      }

      else {
        // Any other unhandled format
        containersOutput.push(c);
      }
    }
  }

  // STEP 3: Build saleData object
  const saleData = {
    date,
    customerName,
    type,
    item,
    quantity: parseInt(quantity),
    totalAmount: parseFloat(totalAmount),
    paymentMethod,
    status,
    remarks,
    customerContainerQty,
    containerIds,       // stores matched container ObjectIds
    containersOutput    // stores readable container codes for display
  };

  // Save to database (adjust this according to your context)
  await Sales.create(saleData);

  console.log("Imported sale:", saleData);
}};






