import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Customers() {
  const [customers, setCustomers] = useState([]);
  const [newCustomer, setNewCustomer] = useState({
    name: '',
    phone: '',
    address: '',
    birthday: '',
    remarks: ''
  });

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error("Error fetching customers:", err);
    }
  };

  const handleChange = (e) => {
    setNewCustomer({ ...newCustomer, [e.target.name]: e.target.value });
  };

  const handleAddCustomer = async () => {
    try {
      await axios.post('http://localhost:3001/api/customers', newCustomer);
      setNewCustomer({ name: '', phone: '', address: '', birthday: '', remarks: '' }); // reset form
      fetchCustomers(); // refresh list
    } catch (err) {
      console.error("Error adding customer:", err);
    }
  };

  return (
    <div>
      <h2>Customers</h2>

      {/* Add Customer Form */}
      <h3>Add New Customer</h3>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={newCustomer.name}
        onChange={handleChange}
      />
      <input
        type="text"
        name="phone"
        placeholder="Phone"
        value={newCustomer.phone}
        onChange={handleChange}
      />
      <input
        type="text"
        name="address"
        placeholder="Address"
        value={newCustomer.address}
        onChange={handleChange}
      />
      <input
        type="date"
        name="birthday"
        value={newCustomer.birthday}
        onChange={handleChange}
      />
      <input
        type="text"
        name="remarks"
        placeholder="Remarks"
        value={newCustomer.remarks}
        onChange={handleChange}
      />
      <button onClick={handleAddCustomer}>Add Customer</button>

      {/* Customer Table */}
      <table border="1" style={{ marginTop: '20px' }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Birthday</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map(c => (
            <tr key={c._id}>
              <td>{c.name}</td>
              <td>{c.phone}</td>
              <td>{c.address}</td>
              <td>{c.birthday ? new Date(c.birthday).toLocaleDateString() : ''}</td>
              <td>{c.remarks}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Customers;
