import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function InventoryDetails() {
  const { id } = useParams();
  const [inventory, setInventory] = useState(null);
  const [sales, setSales] = useState([]);

  useEffect(() => {
    fetchInventory();
    fetchSales();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/inventory/${id}`);
      setInventory(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchSales = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/sales');
      // Filter sales that used this containerId
      const filtered = res.data.filter(s => s.containerId === id);
      setSales(filtered);
    } catch (err) {
      console.error(err);
    }
  };

  if (!inventory) return <div>Loading...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Inventory Details</h2>
      <p><strong>Name:</strong> {inventory.name}</p>
      <p><strong>Status:</strong> {inventory.status}</p>
      <p><strong>Date of Purchase:</strong> {inventory.dateOfPurchase ? new Date(inventory.dateOfPurchase).toLocaleDateString() : ''}</p>
      <p><strong>Remarks:</strong> {inventory.remarks}</p>

      <h4 className="mt-4">Sales Records Using This Container</h4>
      <table className="table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Item</th>
            <th>Quantity</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {sales.map(s => (
            <tr key={s._id}>
              <td>{new Date(s.date).toLocaleDateString()}</td>
              <td>{s.customerId?.name || '-'}</td>
              <td>{s.item}</td>
              <td>{s.quantity}</td>
              <td>â‚±{s.totalAmount}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default InventoryDetails;
