import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';

function Customers() {
    const [editingCustomer, setEditingCustomer] = useState(null);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [customerToDelete, setCustomerToDelete] = useState(null);
    const [customers, setCustomers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [newCustomer, setNewCustomer] = useState({
    name: '',
    
    phone: '',
    address: '',
    birthday: '',
    remarks: ''
  });

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddCustomer = async () => {
    try {
      await axios.post('http://localhost:3001/api/customers', newCustomer);
      setShowModal(false);
      setNewCustomer({ name: '', phone: '', address: '', birthday: '', remarks: '' });
      fetchCustomers();
    } catch (err) {
      console.error(err);
    }
  };
// EDIT Handler
  const handleEditClick = (customer) => {
  setEditingCustomer({ ...customer, birthday: customer.birthday ? customer.birthday.substring(0,10) : '' });
  setShowModal(true);
};

const handleUpdateCustomer = async () => {
  try {
    await axios.put(`http://localhost:3001/api/customers/${editingCustomer._id}`, editingCustomer);
    setShowModal(false);
    setEditingCustomer(null);
    fetchCustomers();
  } catch (err) {
    console.error(err);
  }
};

// DELETE Handler
const handleDeleteClick = (customer) => {
  setCustomerToDelete(customer);
  setShowDeleteModal(true);
};

const handleDeleteCustomer = async () => {
  try {
    await axios.delete(`http://localhost:3001/api/customers/${customerToDelete._id}`);
    setShowDeleteModal(false);
    setCustomerToDelete(null);
    fetchCustomers();
  } catch (err) {
    console.error(err);
  }
};

  return (
    <div style={{ padding: '20px' }}>
      <h2>Customers</h2>
      <Button variant="primary" onClick={() => setShowModal(true)}>Add Customer</Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Birthday</th>
            <th>Remarks</th>
          </tr>
        </thead>
        <tbody>
          {customers.map(c => (
            <tr key={c._id}>
              <td>{c.name}</td>
              <td>{c.phone}</td>
              <td>{c.address}</td>
              <td>{c.birthday ? new Date(c.birthday).toLocaleDateString() : ''}</td>
              <td>{c.remarks}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Customer</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" value={newCustomer.name} onChange={(e) => setNewCustomer({ ...newCustomer, name: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control type="text" value={newCustomer.phone} onChange={(e) => setNewCustomer({ ...newCustomer, phone: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Address</Form.Label>
              <Form.Control type="text" value={newCustomer.address} onChange={(e) => setNewCustomer({ ...newCustomer, address: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Birthday</Form.Label>
              <Form.Control type="date" value={newCustomer.birthday} onChange={(e) => setNewCustomer({ ...newCustomer, birthday: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control as="textarea" rows={2} value={newCustomer.remarks} onChange={(e) => setNewCustomer({ ...newCustomer, remarks: e.target.value })} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleAddCustomer}>Save</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Customers;
