import React, { useEffect, useState } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

function Sales() {
  const [sales, setSales] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const [newSale, setNewSale] = useState({
    date: new Date().toISOString().substr(0, 10),
    customerName: "",
    type: "Walk-in",
    quantity: 1,
    pricePerUnit: 0,
    totalAmount: 0,
    paymentMethod: "Cash",
    status: "Paid",
    remarks: "",
  });

  // Fetch existing sales and customers for autocomplete
  useEffect(() => {
    fetchSales();
    fetchCustomers();
  }, []);

  const fetchSales = async () => {
    const res = await axios.get("http://localhost:3001/api/sales");
    setSales(res.data);
  };

  const fetchCustomers = async () => {
    const res = await axios.get("http://localhost:3001/api/customers");
    setCustomers(res.data);
  };

  const handleSaveSale = async () => {
    try {
      await axios.post("http://localhost:3001/api/sales", newSale);
      setShowModal(false);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };

  // Calculate price per unit dynamically
  const handleTotalAmountChange = (value) => {
    const qty = newSale.quantity || 1;
    const total = parseFloat(value) || 0;
    setNewSale({
      ...newSale,
      totalAmount: total,
      pricePerUnit: (total / qty).toFixed(2),
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Sales</h2>
      <Button variant="primary" onClick={() => setShowModal(true)}>
        Add Sale
      </Button>

      {/* Sales table */}
      <table className="table mt-3">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer Name</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Price/Unit</th>
            <th>Total Amount</th>
            <th>Payment</th>
            <th>Status</th>
            <th>Remarks</th>
          </tr>
        </thead>
        <tbody>
          {sales.map((s) => (
            <tr key={s._id}>
              <td>{new Date(s.date).toLocaleDateString()}</td>
              <td>{s.customerName}</td>
              <td>{s.type}</td>
              <td>{s.quantity}</td>
              <td>{s.pricePerUnit}</td>
              <td>{s.totalAmount}</td>
              <td>{s.paymentMethod}</td>
              <td>{s.status}</td>
              <td>{s.remarks}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Sale Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Sale</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="date"
                value={newSale.date}
                onChange={(e) =>
                  setNewSale({ ...newSale, date: e.target.value })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Customer Name</Form.Label>
              <Form.Control
                type="text"
                value={newSale.customerName}
                onChange={(e) =>
                  setNewSale({ ...newSale, customerName: e.target.value })
                }
                list="customerNames"
              />
              <datalist id="customerNames">
                {customers.map((c) => (
                  <option key={c._id} value={c.name} />
                ))}
              </datalist>
            </Form.Group>

            <Form.Group>
              <Form.Label>Type</Form.Label>
              <br />
              <Button
                variant={
                  newSale.type === "Walk-in" ? "success" : "outline-success"
                }
                onClick={() => setNewSale({ ...newSale, type: "Walk-in" })}
              >
                Walk-in
              </Button>{" "}
              <Button
                variant={
                  newSale.type === "Delivery" ? "primary" : "outline-primary"
                }
                onClick={() => setNewSale({ ...newSale, type: "Delivery" })}
              >
                Delivery
              </Button>
            </Form.Group>

            <Form.Group>
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                value={newSale.quantity}
                onChange={(e) =>
                  setNewSale({
                    ...newSale,
                    quantity: parseInt(e.target.value) || 1,
                  })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Total Amount</Form.Label>
              <Form.Control
                type="number"
                value={newSale.totalAmount}
                onChange={(e) => handleTotalAmountChange(e.target.value)}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Price per Unit</Form.Label>
              <Form.Control
                type="number"
                value={newSale.pricePerUnit}
                readOnly
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Payment Method Used</Form.Label>
              <Form.Control
                as="select"
                value={newSale.paymentMethod}
                onChange={(e) =>
                  setNewSale({ ...newSale, paymentMethod: e.target.value })
                }
              >
                <option>Cash</option>
                <option>Gcash</option>
                <option>Free</option>
              </Form.Control>
            </Form.Group>

            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Control
                as="select"
                value={newSale.status}
                onChange={(e) =>
                  setNewSale({ ...newSale, status: e.target.value })
                }
              >
                <option>Paid</option>
                <option>Unpaid</option>
                <option>Free</option>
              </Form.Control>
            </Form.Group>

            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                value={newSale.remarks}
                onChange={(e) =>
                  setNewSale({ ...newSale, remarks: e.target.value })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSaveSale}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Sales;
