import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import Papa from 'papaparse';

function Customers() {
    const [showImportModal, setShowImportModal] = useState(false);
    const [csvContent, setCsvContent] = useState('');
    const [csvFile, setCsvFile] = useState(null);
    const [customers, setCustomers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [currentCustomer, setCurrentCustomer] = useState({
        name: '', phone: '', address: '', birthday: '', remarks: ''});
    const [isEditing, setIsEditing] = useState(false);
    const [customerToDelete, setCustomerToDelete] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleShowAddModal = () => {
    setIsEditing(false);
    setCurrentCustomer({ name: '', phone: '', address: '', birthday: '', remarks: '' });
    setShowModal(true);
  };

  const handleShowEditModal = (customer) => {
    setIsEditing(true);
    setCurrentCustomer({ ...customer, birthday: customer.birthday ? customer.birthday.substring(0,10) : '' });
    setShowModal(true);
  };

  const handleSaveCustomer = async () => {
    try {
      if (isEditing) {
        await axios.put(`http://localhost:3001/api/customers/${currentCustomer._id}`, currentCustomer);
      } else {
        await axios.post('http://localhost:3001/api/customers', currentCustomer);
      }
      setShowModal(false);
      fetchCustomers();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteClick = (customer) => {
    setCustomerToDelete(customer);
    setShowDeleteModal(true);
  };

  const handleDeleteCustomer = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/customers/${customerToDelete._id}`);
      setShowDeleteModal(false);
      setCustomerToDelete(null);
      fetchCustomers();
    } catch (err) {
      console.error(err);
    }
  };

  // Export as CSV
const handleExportCSV = () => {
  const csv = Papa.unparse(customers.map(c => ({
    name: c.name,
    phone: c.phone,
    address: c.address,
    birthday: c.birthday,
    createdAt: c.createdAt,
    remarks: c.remarks
  })));
  const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.setAttribute('download', 'customers.csv');
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

// Import CSV from pasted content
const handleImportCSVContent = async () => {
  const parsed = Papa.parse(csvContent, { header: true });
  console.log("Parsed content import:", parsed.data);
  try {
    for (let c of parsed.data) {
      if (c.name) {
        await axios.post('http://localhost:3001/api/customers', c);
      }
    }
    fetchCustomers();
    setShowImportModal(false);
    setCsvContent('');
  } catch (err) {
    console.error(err);
  }
};

// Import CSV from file
const handleImportCSVFile = async () => {
  if (!csvFile) return;
  Papa.parse(csvFile, {
    header: true,
    complete: async function(results) {
      console.log("Parsed file import:", results.data);
      try {
        for (let c of results.data) {
          if (c.name) {
            await axios.post('http://localhost:3001/api/customers', c);
          }
        }
        fetchCustomers();
        setShowImportModal(false);
        setCsvFile(null);
      } catch (err) {
        console.error(err);
      }
    }
  });
};


  return (
    <div style={{ padding: '20px' }}>        
        <h2>Customers</h2>
        <Button variant="primary" onClick={handleShowAddModal}>Add Customer</Button>
        <Button variant="success" onClick={handleExportCSV}>Export CSV</Button>{' '}
        <Button variant="secondary" onClick={() => setShowImportModal(true)}>Import CSV</Button>
      <table className="table mt-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Birthday</th>
            <th>Containers</th>
            <th>Created At</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map(c => (
            <tr key={c._id}>
              <td>{c.name}</td>
              <td>{c.phone}</td>
              <td>{c.address}</td>
              <td>{c.birthday ? new Date(c.birthday).toLocaleDateString() : ''}</td>
              <td>{c.containers ? c.containers.length : 0}</td>
              <td>{c.createdAt ? new Date(c.createdAt).toLocaleDateString() : ''}</td>
              <td>{c.remarks}</td>
              <td>
                <Button variant="warning" size="sm" onClick={() => handleShowEditModal(c)}>Edit</Button>{' '}
                <Button variant="danger" size="sm" onClick={() => handleDeleteClick(c)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Import/Export Modal */}
    <Modal show={showImportModal} onHide={() => setShowImportModal(false)}>
        <Modal.Header closeButton>
            <Modal.Title>Import Customers from CSV</Modal.Title>
    </Modal.Header>
    <Modal.Body>
        <Form>
        <Form.Group>
        <Form.Label>Upload CSV File</Form.Label>
        <Form.Control type="file" accept=".csv" onChange={(e) => setCsvFile(e.target.files[0])} />
        <Button variant="primary" className="mt-2" onClick={handleImportCSVFile}>Import File</Button>
      </Form.Group>
      <hr />
      <Form.Group>
        <Form.Label>Or paste CSV content</Form.Label>
        <Form.Control as="textarea" rows={5} value={csvContent} onChange={(e) => setCsvContent(e.target.value)} />
        <Button variant="primary" className="mt-2" onClick={handleImportCSVContent}>Import Pasted Content</Button>
      </Form.Group>
    </Form>
  </Modal.Body>
  <Modal.Footer>
    <Button variant="secondary" onClick={() => setShowImportModal(false)}>Close</Button>
  </Modal.Footer>
</Modal>

      {/* Add/Edit Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Customer' : 'Add Customer'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" value={currentCustomer.name} onChange={(e) => setCurrentCustomer({ ...currentCustomer, name: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control type="text" value={currentCustomer.phone} onChange={(e) => setCurrentCustomer({ ...currentCustomer, phone: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Address</Form.Label>
              <Form.Control type="text" value={currentCustomer.address} onChange={(e) => setCurrentCustomer({ ...currentCustomer, address: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Birthday</Form.Label>
              <Form.Control type="date" value={currentCustomer.birthday} onChange={(e) => setCurrentCustomer({ ...currentCustomer, birthday: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control as="textarea" rows={2} value={currentCustomer.remarks} onChange={(e) => setCurrentCustomer({ ...currentCustomer, remarks: e.target.value })} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSaveCustomer}>Save</Button>
        </Modal.Footer>
      </Modal>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Customer</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete {customerToDelete?.name}?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>Cancel</Button>
          <Button variant="danger" onClick={handleDeleteCustomer}>Delete</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Customers;
