import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';

function Sales() {
  const [sales, setSales] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [salesInputs, setSalesInputs] = useState([]);
  const [customers, setCustomers] = useState([]);

  // =========================
  // FETCH SALES & CUSTOMERS
  // =========================
  useEffect(() => {
    fetchSales();
    fetchCustomers();
  }, []);

  const fetchSales = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/sales');
      setSales(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // =========================
  // HANDLERS
  // =========================
  const handleShowAddModal = () => {
    setSalesInputs([createEmptySale()]);
    setShowModal(true);
  };

  const createEmptySale = () => ({
    date: new Date().toISOString().substr(0,10),
    customerId: '',
    containerId: '',
    type: '',
    item: '',
    quantity: 0,
    pricePerUnit: 0,
    totalAmount: 0,
    paymentMethod: '',
    status: '',
    remarks: ''
  });

  const handleAddAnotherSale = () => {
    setSalesInputs([...salesInputs, createEmptySale()]);
  };

  const handleSaleChange = (index, field, value) => {
    const updated = [...salesInputs];
    updated[index][field] = value;
    if (field === 'totalAmount' && updated[index].quantity > 0) {
      updated[index].pricePerUnit = parseFloat(value) / updated[index].quantity;
    }
    setSalesInputs(updated);
  };

  const handleSaveSales = async () => {
    try {
      for (let sale of salesInputs) {
        await axios.post('http://localhost:3001/api/sales', sale);
      }
      setShowModal(false);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };

  // =========================
  // RENDER
  // =========================
  return (
    <div style={{ padding: '20px' }}>
      <h2>Sales</h2>
      <Button variant="primary" onClick={handleShowAddModal}>Add Sales</Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Item</th>
            <th>Quantity</th>
            <th style={{ color: 'gray' }}>Price/Unit</th>
            <th>Total</th>
            <th>Payment</th>
            <th>Status</th>
            <th><i>Remarks</i></th>
          </tr>
        </thead>
        <tbody>
          {sales.map(s => (
            <tr key={s._id}>
              <td>{s.date ? new Date(s.date).toLocaleDateString() : ''}</td>
              <td>{s.customerId?.name || ''}</td>
              <td>{s.type}</td>
              <td>{s.item}</td>
              <td>{s.quantity}</td>
              <td style={{ color: 'gray' }}>{s.pricePerUnit}</td>
              <td>{s.totalAmount}</td>
              <td>{s.paymentMethod}</td>
              <td>{s.status}</td>
              <td><i>{s.remarks}</i></td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ADD MULTIPLE SALES MODAL */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Add Multiple Sales</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {salesInputs.map((sale, index) => (
            <div key={index} style={{ borderBottom: '1px solid #ccc', marginBottom: '10px', paddingBottom: '10px' }}>
              <Form>
                <Form.Group>
                  <Form.Label>Date</Form.Label>
                  <Form.Control type="date" value={sale.date}
                    onChange={(e) => handleSaleChange(index, 'date', e.target.value)} />
                </Form.Group>

                <Form.Group>
                  <Form.Label>Customer</Form.Label>
                  <Form.Control type="text" value={sale.customerId}
                    onChange={(e) => handleSaleChange(index, 'customerId', e.target.value)}
                    placeholder="Start typing name" />
                </Form.Group>

                <Form.Group>
                  <Form.Label>Type</Form.Label>
                  <div>
                    <Button variant="success" size="sm"
                      onClick={() => handleSaleChange(index, 'type', 'Walk-in')}
                      active={sale.type === 'Walk-in'}>Walk-in</Button>{' '}
                    <Button variant="primary" size="sm"
                      onClick={() => handleSaleChange(index, 'type', 'Delivery')}
                      active={sale.type === 'Delivery'}>Delivery</Button>
                  </div>
                </Form.Group>

                <Form.Group>
                  <Form.Label>Quantity</Form.Label>
                  <Form.Control type="number" value={sale.quantity}
                    onChange={(e) => handleSaleChange(index, 'quantity', e.target.value)} />
                </Form.Group>

                <Form.Group>
                  <Form.Label>Total Amount</Form.Label>
                  <Form.Control type="number" value={sale.totalAmount}
                    onChange={(e) => handleSaleChange(index, 'totalAmount', e.target.value)} />
                </Form.Group>

                <Form.Group>
                  <Form.Label>Payment Method</Form.Label>
                  <Form.Control as="select" value={sale.paymentMethod}
                    onChange={(e) => handleSaleChange(index, 'paymentMethod', e.target.value)}>
                    <option value="">Select</option>
                    <option>Cash</option>
                    <option>GCash</option>
                    <option>Free</option>
                  </Form.Control>
                </Form.Group>

                <Form.Group>
                  <Form.Label>Status</Form.Label>
                  <Form.Control as="select" value={sale.status}
                    onChange={(e) => handleSaleChange(index, 'status', e.target.value)}>
                    <option value="">Select</option>
                    <option>Paid</option>
                    <option>Unpaid</option>
                    <option>Free</option>
                  </Form.Control>
                </Form.Group>

                <Form.Group>
                  <Form.Label>Remarks</Form.Label>
                  <Form.Control as="textarea" rows={2}
                    value={sale.remarks}
                    onChange={(e) => handleSaleChange(index, 'remarks', e.target.value)} />
                </Form.Group>
              </Form>
            </div>
          ))}

          <Button variant="secondary" onClick={handleAddAnotherSale}>Add Another Sale</Button>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSaveSales}>Save All</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Sales;
