import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import { FaEdit, FaTrash } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom'; // ðŸ†• import

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentInventory, setCurrentInventory] = useState({
    id: '',
    name: '',
    status: 'Stock In',
    dateOfPurchase: '',
    remarks: '',
  });
  const [showMultiAddModal, setShowMultiAddModal] = useState(false);
  const [multiInput, setMultiInput] = useState('');

  const navigate = useNavigate(); // ðŸ†• hook

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/inventory');
      setInventory(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // ... other functions remain the same

  return (
    <div style={{ padding: '20px' }}>
      <h2>Inventory</h2>

      <Button variant="primary" onClick={handleShowAddModal}>
        Add Inventory
      </Button>{' '}
      <Button variant="secondary" onClick={() => setShowMultiAddModal(true)}>
        Add Multiple
      </Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Status</th>
            <th>Date of Purchase</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((inv) => (
            <tr key={inv._id}>
              <td>{inv.id}</td>
              <td
                style={{ cursor: 'pointer', textDecoration: 'underline' }}
                onClick={() => navigate(`/inventory/${inv._id}`)} // ðŸ†• navigate to details page
              >
                {inv.name}
              </td>
              <td>{inv.status}</td>
              <td>
                {inv.dateOfPurchase
                  ? new Date(inv.dateOfPurchase).toLocaleDateString()
                  : '-'}
              </td>
              <td>{inv.remarks}</td>
              <td>
                <Button
                  variant="link"
                  size="sm"
                  onClick={() => handleEdit(inv)}
                >
                  <FaEdit color="orange" />
                </Button>
                <Button
                  variant="link"
                  size="sm"
                  onClick={() => handleDelete(inv._id)}
                >
                  <FaTrash color="red" />
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Your modals remain the same */}
      {/* ... */}
    </div>
  );
}

export default Inventory;
