import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';

function InventoryDetails() {
  const { id } = useParams();
  const [inventory, setInventory] = useState(null);
  const [sales, setSales] = useState([]);

  useEffect(() => {
    fetchInventory();
  }, [id]);

  const fetchInventory = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/inventory/${id}`);
      setInventory(res.data);

      const salesRes = await axios.get(`http://localhost:3001/api/sales/by-container/${res.data.name}`);
      setSales(salesRes.data);
    } catch (err) {
      console.error(err);
    }
  };

  if (!inventory) return <p>Loading...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Inventory Details</h2>
      <Link to="/inventory"><Button variant="secondary">Back to Inventory</Button></Link>

      <div className="mt-3">
        <p><strong>Name:</strong> <h2>{inventory.name}</h2></p>
        <p><strong>ID:</strong> {inventory.id}</p>
        <p><strong>Status:</strong> {inventory.status}</p>
        <p><strong>Date of Purchase:</strong> {inventory.dateOfPurchase ? new Date(inventory.dateOfPurchase).toLocaleDateString() : '-'}</p>
        <p><strong>Remarks:</strong> {inventory.remarks}</p>
      </div>

      <h4 className="mt-4">Sales Records for {inventory.name}</h4>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Total Amount</th>
          </tr>
        </thead>
        <tbody>
          {sales.map(s => (
            <tr key={s._id}>
              <td>{new Date(s.date).toLocaleDateString()}</td>
              <td>{s.customerId?.name || '-'}</td>
              <td>{s.type}</td>
              <td>{s.quantity}</td>
              <td>â‚±{s.totalAmount}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default InventoryDetails;
