import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import { Typeahead } from 'react-bootstrap-typeahead';
import { FaEdit, FaTrash } from 'react-icons/fa';

function Sales() {
  const [sales, setSales] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  const [currentSale, setCurrentSale] = useState({
    date: new Date().toISOString().split('T')[0],
    customerId: '',
    containerId: '',
    type: 'Delivery',
    item: '',
    quantity: 1,
    pricePerUnit: 0,
    totalAmount: 0,
    paymentMethod: 'Cash',
    status: 'Paid',
    remarks: ''
  });

  const [saleToEditId, setSaleToEditId] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [saleToDelete, setSaleToDelete] = useState(null);
  const [showMultiAddModal, setShowMultiAddModal] = useState(false);
  const [multiSalesInput, setMultiSalesInput] = useState('');

  // ===============================
  // LOAD SALES & CUSTOMERS
  // ===============================
  useEffect(() => {
    fetchSales();
    fetchCustomers();
  }, []);

  const fetchSales = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/sales');
      setSales(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // ===============================
  // HANDLERS
  // ===============================
  const handleShowAddModal = () => {
    setIsEditing(false);
    setCurrentSale({
      date: new Date().toISOString().split('T')[0],
      customerId: '',
      containerId: '',
      type: 'Delivery',
      item: '',
      quantity: 1,
      pricePerUnit: 0,
      totalAmount: 0,
      paymentMethod: 'Cash',
      status: 'Paid',
      remarks: ''
    });
    setShowModal(true);
  };

  const handleShowEditModal = (sale) => {
    setIsEditing(true);
    setSaleToEditId(sale._id);
    setCurrentSale({
      date: sale.date ? sale.date.substring(0,10) : new Date().toISOString().split('T')[0],
      customerId: sale.customerId?._id || '',
      containerId: sale.containerId || '',
      type: sale.type,
      item: sale.item || '',
      quantity: sale.quantity,
      pricePerUnit: sale.pricePerUnit || 0,
      totalAmount: sale.totalAmount,
      paymentMethod: sale.paymentMethod,
      status: sale.status,
      remarks: sale.remarks || ''
    });
    setShowModal(true);
  };

  const handleSaveSale = async () => {
    try {
      const calculatedPricePerUnit = currentSale.totalAmount && currentSale.quantity
        ? currentSale.totalAmount / currentSale.quantity
        : 0;

      const saleToSave = { ...currentSale, pricePerUnit: calculatedPricePerUnit };

      if (isEditing) {
        await axios.put(`http://localhost:3001/api/sales/${saleToEditId}`, saleToSave);
      } else {
        await axios.post('http://localhost:3001/api/sales', saleToSave);
      }

      setShowModal(false);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteClick = (sale) => {
    setSaleToDelete(sale);
    setShowDeleteModal(true);
  };

  const handleDeleteSale = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/sales/${saleToDelete._id}`);
      setShowDeleteModal(false);
      setSaleToDelete(null);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };

const handleMultiSalesImport = async () => {
  if (!multiSalesInput) {
    alert('Please enter sales records first.');
    return;
  }

  const lines = multiSalesInput.trim().split('\n');
  let successCount = 0;

  try {
    for (let line of lines) {
      const [date, customerName, type, quantity, totalAmount, paymentMethod, status, remarks] = line.split(';').map(f => f.trim());

      // Find customerId by customer name (case insensitive)
      const customer = customers.find(c => c.name.toLowerCase() === customerName.toLowerCase());
      const qty = parseInt(quantity);
      const total = parseFloat(totalAmount);
      const pricePerUnit = qty !== 0 ? total / qty : 0;

      const sale = {
        date: date || new Date().toISOString().split('T')[0],
        customerId: customer ? customer._id : null,
        type: type || 'Walk-in',
        quantity: qty || 1,
        totalAmount: total || 0,
        pricePerUnit: pricePerUnit || 0,
        paymentMethod: paymentMethod || 'Cash',
        status: status || 'Paid',
        remarks: `${remarks || ''}${customer ? '' : ` | CustomerName: ${customerName}`}`.trim()
      };

      await axios.post('http://localhost:3001/api/sales', sale);
      successCount++;
    }

    alert(`Import completed!\nSuccessfully added: ${successCount}`);
    setShowMultiAddModal(false);
    setMultiSalesInput('');
    fetchSales();
  } catch (err) {
    console.error(err);
    alert('Error importing sales. Check console for details.');
  }
};


  // ===============================
  // RENDER
  // ===============================
  return (
    <div style={{ padding: '20px' }}>
      <h2>Sales</h2>

      <Button variant="primary" onClick={handleShowAddModal}>Add Sale</Button>
      <Button variant="secondary" onClick={() => setShowMultiAddModal(true)}>Add Multiple Sales</Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Price Per Unit</th>
            <th>Total</th>
            <th>Payment</th>
            <th>Status</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {sales.map(s => (
            <tr key={s._id}>
              <td>{new Date(s.date).toLocaleDateString()}</td>
              <td>{s.customerId?.name || '-'}</td>
              <td>{s.type}</td>
              <td>{s.quantity}</td>
              <td className="text-muted">₱{s.pricePerUnit?.toFixed(2) || '0.00'}</td>
              <td>₱{s.totalAmount}</td>
              <td>{s.paymentMethod}</td>
              <td>{s.status}</td>
              <td className="fst-italic">{s.remarks}</td>
              <td>
<Button variant="warning" size="sm" style={{ width: '25px', height: '25px', padding: '10' }} onClick={() => handleShowEditModal(s)}>
  <FaEdit />
</Button>

<Button variant="danger" size="sm" style={{ width: '25px', height: '25px', padding: '10' }} onClick={() => handleDeleteClick(s)}>
  <FaTrash />
</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Sale' : 'Add Sale'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="date"
                value={currentSale.date}
                onChange={(e) => setCurrentSale({ ...currentSale, date: e.target.value })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Customer Name</Form.Label>
              <Typeahead
                id="customer-typeahead"
                labelKey="name"
                options={customers}
                placeholder="Type customer name..."
                onChange={(selected) => {
                  if (selected.length > 0) {
                    setCurrentSale({ ...currentSale, customerId: selected[0]._id });
                  } else {
                    setCurrentSale({ ...currentSale, customerId: '' });
                  }
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Type</Form.Label><br/>
              <Button
                variant={currentSale.type === 'Walk-in' ? 'success' : 'outline-success'}
                onClick={() => setCurrentSale({ ...currentSale, type: 'Walk-in' })}
                className="me-2"
              >
                Walk-in
              </Button>
              <Button
                variant={currentSale.type === 'Delivery' ? 'primary' : 'outline-primary'}
                onClick={() => setCurrentSale({ ...currentSale, type: 'Delivery' })}
              >
                Delivery
              </Button>
            </Form.Group>

            <Form.Group>
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                value={currentSale.quantity}
                onChange={(e) => setCurrentSale({ ...currentSale, quantity: Number(e.target.value) })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Total Amount</Form.Label>
              <Form.Control
                type="number"
                value={currentSale.totalAmount}
                onChange={(e) => setCurrentSale({ ...currentSale, totalAmount: Number(e.target.value) })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Payment Method</Form.Label>
              <Form.Select
                value={currentSale.paymentMethod}
                onChange={(e) => setCurrentSale({ ...currentSale, paymentMethod: e.target.value })}
              >
                <option>Cash</option>
                <option>GCash</option>
                <option>Free</option>
              </Form.Select>
            </Form.Group>

            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={currentSale.status}
                onChange={(e) => setCurrentSale({ ...currentSale, status: e.target.value })}
              >
                <option>Paid</option>
                <option>Unpaid</option>
                <option>Free</option>
              </Form.Select>
            </Form.Group>

            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                value={currentSale.remarks}
                onChange={(e) => setCurrentSale({ ...currentSale, remarks: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSaveSale}>Save</Button>
        </Modal.Footer>
      </Modal>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Sale</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this sale?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>Cancel</Button>
          <Button variant="danger" onClick={handleDeleteSale}>Delete</Button>
        </Modal.Footer>
      </Modal>

      {/* Add Multi Sales Modal */}
        <Modal show={showMultiAddModal} onHide={() => setMultiSalesInput(false)}>
            <Modal.Header closeButton>
                <Modal.Title>Add Multiple Sales</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group>
                        <Form.Label>Enter multiple sales (one per line, semicolon-separated fields: date;customer name;type;quantity;total amount;payment method;status;remarks)</Form.Label>
                        <Form.Control as="textarea" rows={10} value={multiSalesInput} onChange={(e) => setMultiSalesInput(e.target.value)} />
                    </Form.Group>
                    <Button variant="primary" className="mt-2" onClick={handleMultiSalesImport}>Import Sales</Button>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={() => setShowMultiAddModal(false)}>Close</Button>
            </Modal.Footer>
            </Modal>
    </div>
  );
}

export default Sales;
