import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FaEdit, FaTrash } from 'react-icons/fa';

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showMultiAddModal, setShowMultiAddModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteTargetId, setDeleteTargetId] = useState(null);
  const [currentItem, setCurrentItem] = useState({
    id: null,
    name: '',
    type:'',
    status: 'Stock In',
    dateOfPurchase: new Date().toISOString().split('T')[0],
    remarks: ''
  });
  const [multiInput, setMultiInput] = useState('');
  const [checkAll, setCheckAll] = useState(false);


  // ================================
  // FETCH INVENTORY
  // ================================
  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/inventory');
      setInventory(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // ================================
  // ADD or UPDATE INVENTORY
  // ================================
  const handleSave = async () => {
    try {
      if (currentItem._id) {
        // EDIT
        await axios.put(`http://localhost:3001/api/inventory/${currentItem._id}`, currentItem);
      } else {
        // ADD
        await axios.post('http://localhost:3001/api/inventory', currentItem);
      }
      setShowModal(false);
      fetchInventory();
    } catch (err) {
      console.error(err);
    }
  };

  // ================================
  // DELETE INVENTORY
  // ================================
const confirmDelete = async () => {
  try {
    await axios.delete(`http://localhost:3001/api/inventory/${deleteTargetId}`);
    setShowDeleteModal(false);
    setDeleteTargetId(null);
    fetchInventory();
  } catch (err) {
    console.error(err);
  }
};

  // ================================
  // ADD MULTIPLE INVENTORY
  // ================================
  const handleMultiAdd = async () => {
    const lines = multiInput.trim().split('\n');
    try {
      for (let line of lines) {
        const [id, name, type, status, dateOfPurchase, remarks] = line.split(';').map(f => f.trim());
        const item = {
          id: Number(id),
          name,
          type,
          status: status || 'Stock In',
          dateOfPurchase: dateOfPurchase ? new Date(dateOfPurchase) : new Date(),
          remarks: remarks || ''
        };
        await axios.post('http://localhost:3001/api/inventory', item);
      }
      alert('Import successful!');
      setShowMultiAddModal(false);
      setMultiInput('');
      fetchInventory();
    } catch (err) {
      console.error(err);
      alert('Error importing inventory.');
    }
  };

  // ================================
  // RENDER
  // ================================
  return (
    <div style={{ padding: '20px' }}>
      <h2>Inventory</h2>

      <Button variant="primary" onClick={() => {
        setCurrentItem({
          id: null,
          name: '',
          type: '',
          status: 'Stock In',
          dateOfPurchase: new Date().toISOString().split('T')[0],
          remarks: ''
        });
        setShowModal(true);
      }}>Add Inventory</Button>

      <Button variant="secondary" onClick={() => setShowMultiAddModal(true)} className="ms-2">Add Multiple Inventory</Button>
      <Button
        variant={editMode ? "secondary" : "warning"}
        className="ms-2"
        onClick={() => {
            setEditMode(!editMode);
            setSelectedIds([]);
            setCheckAll(false);
        }}
      >
      {editMode ? "Exit Edit Mode" : "Edit Mode"}
      </Button>

      {editMode && (
  <Button
    variant="danger"
    className="ms-2"
    disabled={selectedIds.length === 0}
    onClick={async () => {
      if (!window.confirm(`Delete ${selectedIds.length} items?`)) return;
      try {
        await Promise.all(selectedIds.map(id => axios.delete(`http://localhost:3001/api/inventory/${id}`)));
        fetchInventory();
        setSelectedIds([]);
        setCheckAll(false);
      } catch (err) {
        console.error(err);
        alert('Error deleting selected items.');
      }
    }}
  >
    Delete Selected ({selectedIds.length})
  </Button>
)}

      <table className="table mt-3">
        <thead>
          <tr>
                {editMode && (
      <th>
        <Form.Check
          type="checkbox"
          checked={checkAll}
          onChange={(e) => {
            setCheckAll(e.target.checked);
            if (e.target.checked) {
              setSelectedIds(inventory.map(inv => inv._id));
            } else {
              setSelectedIds([]);
            }
          }}
        />
      </th>
    )}
            <th>ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Status</th>
            <th>Date of Purchase</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
  {inventory.map(inv => {
    const isChecked = selectedIds.includes(inv._id);
    return (
      <tr key={inv._id}>
        {editMode && (
          <td>
            <Form.Check
              type="checkbox"
              checked={isChecked}
              onChange={(e) => {
                if (e.target.checked) {
                  setSelectedIds(prev => [...prev, inv._id]);
                } else {
                  setSelectedIds(prev => prev.filter(id => id !== inv._id));
                  setCheckAll(false);
                }
              }}
            />
          </td>
        )}
        <td>{inv.id}</td>
        <td>
          <Link to={`/inventory/${inv._id}`}>{inv.name}</Link>
        </td>
        <td>{inv.type}</td>
        <td>{inv.status}</td>
        <td>{inv.dateOfPurchase ? new Date(inv.dateOfPurchase).toLocaleDateString() : ''}</td>
        <td className="fst-italic">{inv.remarks}</td>
        <td>
          <FaEdit
            style={{ cursor: 'pointer', color: 'orange', marginRight: '10px' }}
            onClick={() => {
              setCurrentItem({
                _id: inv._id,
                id: inv.id,
                name: inv.name,
                type: inv.type,
                status: inv.status,
                dateOfPurchase: inv.dateOfPurchase ? new Date(inv.dateOfPurchase).toISOString().split('T')[0] : '',
                remarks: inv.remarks
              });
              setShowModal(true);
            }}
          />
          <FaTrash
            style={{ cursor: 'pointer', color: 'red' }}
            onClick={() => {
              setDeleteTargetId(inv._id);
              setShowDeleteModal(true);
            }}
          />
        </td>
      </tr>
    )
  })}
</tbody>

      </table>

      {/* Add/Edit Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{currentItem._id ? 'Edit Inventory' : 'Add Inventory'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>ID</Form.Label>
              <Form.Control
                type="number"
                value={currentItem.id || ''}
                onChange={(e) => setCurrentItem({ ...currentItem, id: Number(e.target.value) })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={currentItem.name}
                onChange={(e) => setCurrentItem({ ...currentItem, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
                <Form.Label>Type</Form.Label>
                <div className="d-flex flex-wrap">
                  {[
                    { label: "Slim", bg: "primary" },
                    { label: "Round", bg: "info" },
                    { label: "Dispenser", bg: "success" },
                  ].map(type => {
                    const isSelected = currentItem.type === type.label;
                    return (
                      <Button
                        key={type.label}
                        size="sm"
                        variant={isSelected ? type.bg : `outline-${type.bg}`}
                        className="me-2 mb-2"
                        onClick={() => setCurrentItem({ ...currentItem, type: type.label })}
                        style={{
                          backgroundColor: type.bg === "navy blue" && isSelected ? "#2b35c4ff"
                                           : type.bg === "navy blue" ? "transparent"
                                           : undefined,
                          borderColor: type.bg === "navy blue" ? "#1b206bff" : undefined,
                          color: type.bg === "navy blue" && isSelected ? "#636dfeff"
                                : type.bg === "purple" ? "#636dfeff"
                                : undefined,
                        }}
                      >
                        {type.label}
                      </Button>
                    )
                  })}
                </div>
            </Form.Group>

            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={currentItem.status}
                onChange={(e) => setCurrentItem({ ...currentItem, status: e.target.value })}
              >
                <option>Stock In</option>
                <option>Issued</option>
                <option>Lost</option>
                <option>Repaired</option>
                <option>Damaged</option>
              </Form.Select>
            </Form.Group>
            <Form.Group>
              <Form.Label>Date of Purchase</Form.Label>
              <Form.Control
                type="date"
                value={currentItem.dateOfPurchase}
                onChange={(e) => setCurrentItem({ ...currentItem, dateOfPurchase: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                value={currentItem.remarks}
                onChange={(e) => setCurrentItem({ ...currentItem, remarks: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSave}>Save</Button>
        </Modal.Footer>
      </Modal>

      {/* Multi Add Modal */}
      <Modal show={showMultiAddModal} onHide={() => setShowMultiAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Multiple Inventory</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Enter multiple inventory records (one per line, semicolon-separated fields: id;name;type;status;dateOfPurchase;remarks)</Form.Label>
              <Form.Control
                as="textarea"
                rows={10}
                value={multiInput}
                onChange={(e) => setMultiInput(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" className="mt-2" onClick={handleMultiAdd}>Import Inventory</Button>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowMultiAddModal(false)}>Close</Button>
        </Modal.Footer>
      </Modal>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
            <Modal.Title>Confirm Delete</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            Are you sure you want to delete this inventory item?
        </Modal.Body>
        <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
                Cancel
            </Button>
            <Button variant="danger" onClick={confirmDelete}>
                Delete
            </Button>
        </Modal.Footer>
    </Modal>
    </div>
  );
}

export default Inventory;
