import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';

function Sales() {
  // ===============================
  // STATES
  // ===============================
  const [sales, setSales] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const [currentSale, setCurrentSale] = useState({
    date: new Date().toISOString().split('T')[0],
    customerId: '',
    containerId: '', // skip for now
    type: 'Walk-in',
    item: '',
    quantity: 1,
    pricePerUnit: 0,
    totalAmount: 0,
    paymentMethod: 'Cash',
    status: 'Paid',
    remarks: ''
  });

  // ===============================
  // LOAD SALES & CUSTOMERS
  // ===============================
  useEffect(() => {
    fetchSales();
    fetchCustomers();
  }, []);

  const fetchSales = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/sales');
      setSales(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchCustomers = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/customers');
      setCustomers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // ===============================
  // HANDLERS
  // ===============================
  const handleShowModal = () => {
    setCurrentSale({
      date: new Date().toISOString().split('T')[0],
      customerId: '',
      containerId: '',
      type: 'Walk-in',
      item: '',
      quantity: 1,
      pricePerUnit: 0,
      totalAmount: 0,
      paymentMethod: 'Cash',
      status: 'Paid',
      remarks: ''
    });
    setShowModal(true);
  };

  const handleSaveSale = async () => {
    try {
      const calculatedPricePerUnit = currentSale.totalAmount && currentSale.quantity
        ? currentSale.totalAmount / currentSale.quantity
        : 0;

      const saleToSave = { ...currentSale, pricePerUnit: calculatedPricePerUnit };

      await axios.post('http://localhost:3001/api/sales', saleToSave);

      setShowModal(false);
      fetchSales();
    } catch (err) {
      console.error(err);
    }
  };

  // ===============================
  // SUGGEST CUSTOMER NAMES (BASIC)
  // ===============================
  const handleCustomerInput = (e) => {
    const input = e.target.value;

    // Find first match from loaded customers
    const found = customers.find(c =>
      c.name.toLowerCase().includes(input.toLowerCase())
    );

    if (found) {
      setCurrentSale({ ...currentSale, customerId: found._id });
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Sales</h2>

      <Button variant="primary" onClick={handleShowModal}>Add Sale</Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Total</th>
            <th>Payment</th>
            <th>Status</th>
            <th>Remarks</th>
          </tr>
        </thead>
        <tbody>
          {sales.map(s => (
            <tr key={s._id}>
              <td>{new Date(s.date).toLocaleDateString()}</td>
              <td>{s.customerId?.name || '-'}</td>
              <td>{s.type}</td>
              <td>{s.quantity}</td>
              <td>₱{s.totalAmount}</td>
              <td>{s.paymentMethod}</td>
              <td>{s.status}</td>
              <td>{s.remarks}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Sale Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Sale</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="date"
                value={currentSale.date}
                onChange={(e) => setCurrentSale({ ...currentSale, date: e.target.value })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Customer Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Type to search..."
                onChange={handleCustomerInput}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Type</Form.Label><br/>
              <Button
                variant={currentSale.type === 'Walk-in' ? 'success' : 'outline-success'}
                onClick={() => setCurrentSale({ ...currentSale, type: 'Walk-in' })}
                className="me-2"
              >
                Walk-in
              </Button>
              <Button
                variant={currentSale.type === 'Delivery' ? 'primary' : 'outline-primary'}
                onClick={() => setCurrentSale({ ...currentSale, type: 'Delivery' })}
              >
                Delivery
              </Button>
            </Form.Group>

            <Form.Group>
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                value={currentSale.quantity}
                onChange={(e) => setCurrentSale({ ...currentSale, quantity: Number(e.target.value) })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Total Amount</Form.Label>
              <Form.Control
                type="number"
                value={currentSale.totalAmount}
                onChange={(e) => setCurrentSale({ ...currentSale, totalAmount: Number(e.target.value) })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Payment Method</Form.Label>
              <Form.Select
                value={currentSale.paymentMethod}
                onChange={(e) => setCurrentSale({ ...currentSale, paymentMethod: e.target.value })}
              >
                <option>Cash</option>
                <option>GCash</option>
                <option>Free</option>
              </Form.Select>
            </Form.Group>

            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={currentSale.status}
                onChange={(e) => setCurrentSale({ ...currentSale, status: e.target.value })}
              >
                <option>Paid</option>
                <option>Unpaid</option>
                <option>Free</option>
              </Form.Select>
            </Form.Group>

            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                value={currentSale.remarks}
                onChange={(e) => setCurrentSale({ ...currentSale, remarks: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSaveSale}>Save Sale</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Sales;
