import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import { FaEdit, FaTrash } from 'react-icons/fa';

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentInventory, setCurrentInventory] = useState({
    id: '',
    name: '',
    status: 'Stock In',
    dateOfPurchase: '',
    remarks: '',
  });
  const [showMultiAddModal, setShowMultiAddModal] = useState(false);
  const [multiInput, setMultiInput] = useState('');

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/inventory');
      setInventory(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleShowAddModal = () => {
    setIsEditing(false);
    setCurrentInventory({
      id: '',
      name: '',
      status: 'Stock In',
      dateOfPurchase: '',
      remarks: '',
    });
    setShowModal(true);
  };

  const handleEdit = (inv) => {
    setIsEditing(true);
    setCurrentInventory({
      ...inv,
      dateOfPurchase: inv.dateOfPurchase
        ? new Date(inv.dateOfPurchase).toISOString().split('T')[0]
        : '',
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this record?')) {
      try {
        await axios.delete(`http://localhost:3001/api/inventory/${id}`);
        fetchInventory();
      } catch (err) {
        console.error(err);
        alert('Delete failed.');
      }
    }
  };

  const handleSave = async () => {
    try {
      if (isEditing) {
        await axios.put(
          `http://localhost:3001/api/inventory/${currentInventory._id}`,
          currentInventory
        );
      } else {
        await axios.post('http://localhost:3001/api/inventory', currentInventory);
      }
      setShowModal(false);
      fetchInventory();
    } catch (err) {
      console.error(err);
      alert('Save failed.');
    }
  };

  const handleMultiAdd = async () => {
    const lines = multiInput.trim().split('\n');
    try {
      for (let line of lines) {
        const [name, status, dateOfPurchase, remarks] = line
          .split(';')
          .map((f) => f.trim());
        const record = {
          name,
          status: status || 'Stock In',
          dateOfPurchase: dateOfPurchase || null,
          remarks: remarks || '',
        };
        await axios.post('http://localhost:3001/api/inventory', record);
      }
      setShowMultiAddModal(false);
      setMultiInput('');
      fetchInventory();
      alert('Multiple inventory records added!');
    } catch (err) {
      console.error(err);
      alert('Import failed.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Inventory</h2>

      <Button variant="primary" onClick={handleShowAddModal}>
        Add Inventory
      </Button>{' '}
      <Button variant="secondary" onClick={() => setShowMultiAddModal(true)}>
        Add Multiple
      </Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Status</th>
            <th>Date of Purchase</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((inv) => (
            <tr key={inv._id}>
              <td>{inv.id}</td>
                            <td><Link to={`/inventory/${item._id}`}>{item.name}</Link></td>
              <td>{inv.status}</td>
              <td>
                {inv.dateOfPurchase
                  ? new Date(inv.dateOfPurchase).toLocaleDateString()
                  : '-'}
              </td>
              <td>{inv.remarks}</td>
              <td>
                <Button
                  variant="link"
                  size="sm"
                  onClick={() => handleEdit(inv)}
                >
                  <FaEdit color="orange" />
                </Button>
                <Button
                  variant="link"
                  size="sm"
                  onClick={() => handleDelete(inv._id)}
                >
                  <FaTrash color="red" />
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Inventory' : 'Add Inventory'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>ID</Form.Label>
              <Form.Control
                type="number"
                value={currentInventory.id}
                onChange={(e) =>
                  setCurrentInventory({ ...currentInventory, id: e.target.value })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={currentInventory.name}
                onChange={(e) =>
                  setCurrentInventory({ ...currentInventory, name: e.target.value })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={currentInventory.status}
                onChange={(e) =>
                  setCurrentInventory({ ...currentInventory, status: e.target.value })
                }
              >
                <option>Stock In</option>
                <option>Issued</option>
                <option>Lost</option>
                <option>Repaired</option>
                <option>Damaged</option>
              </Form.Select>
            </Form.Group>

            <Form.Group>
              <Form.Label>Date of Purchase</Form.Label>
              <Form.Control
                type="date"
                value={currentInventory.dateOfPurchase}
                onChange={(e) =>
                  setCurrentInventory({
                    ...currentInventory,
                    dateOfPurchase: e.target.value,
                  })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                value={currentInventory.remarks}
                onChange={(e) =>
                  setCurrentInventory({ ...currentInventory, remarks: e.target.value })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Add Multiple Modal */}
      <Modal show={showMultiAddModal} onHide={() => setShowMultiAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Multiple Inventory</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>
              Enter multiple inventory records (one per line, semicolon-separated:
              name;status;dateOfPurchase;remarks)
            </Form.Label>
            <Form.Control
              as="textarea"
              rows={10}
              value={multiInput}
              onChange={(e) => setMultiInput(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" className="mt-2" onClick={handleMultiAdd}>
            Import Inventory
          </Button>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowMultiAddModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Inventory;
