import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentItem, setCurrentItem] = useState({
    id: '',
    name: '',
    status: 'Stock In',
    dateOfPurchase: '',
    remarks: ''
  });

  const [itemToEditId, setItemToEditId] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [showMultiAddModal, setShowMultiAddModal] = useState(false);
  const [multiInput, setMultiInput] = useState('');

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/inventory');
      setInventory(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleShowAddModal = () => {
    setIsEditing(false);
    setCurrentItem({
      id: '',
      name: '',
      status: 'Stock In',
      dateOfPurchase: '',
      remarks: ''
    });
    setShowModal(true);
  };

  const handleShowEditModal = (item) => {
    setIsEditing(true);
    setItemToEditId(item._id);
    setCurrentItem({
      id: item.id,
      name: item.name,
      status: item.status,
      dateOfPurchase: item.dateOfPurchase ? item.dateOfPurchase.substring(0,10) : '',
      remarks: item.remarks || ''
    });
    setShowModal(true);
  };

  const handleSaveItem = async () => {
    try {
      if (isEditing) {
        await axios.put(`http://localhost:3001/api/inventory/${itemToEditId}`, currentItem);
      } else {
        await axios.post('http://localhost:3001/api/inventory', currentItem);
      }
      setShowModal(false);
      fetchInventory();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteClick = (item) => {
    setItemToDelete(item);
    setShowDeleteModal(true);
  };

  const handleDeleteItem = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/inventory/${itemToDelete._id}`);
      setShowDeleteModal(false);
      setItemToDelete(null);
      fetchInventory();
    } catch (err) {
      console.error(err);
    }
  };

  const handleMultiImport = async () => {
    if (!multiInput) {
      alert('Please enter inventory records first.');
      return;
    }

    const lines = multiInput.trim().split('\n');
    let successCount = 0;

    try {
      for (let line of lines) {
        const [id, name, status, dateOfPurchase, remarks] = line.split(';').map(f => f.trim());
        const item = {
          id: parseInt(id),
          name,
          status: status || 'Stock In',
          dateOfPurchase: dateOfPurchase ? new Date(dateOfPurchase) : null,
          remarks: remarks || ''
        };
        await axios.post('http://localhost:3001/api/inventory', item);
        successCount++;
      }

      alert(`Import completed!\nSuccessfully added: ${successCount}`);
      setShowMultiAddModal(false);
      setMultiInput('');
      fetchInventory();
    } catch (err) {
      console.error(err);
      alert('Error importing inventory. Check console for details.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Inventory</h2>

      <Button variant="primary" onClick={handleShowAddModal}>Add Item</Button>
      <Button variant="secondary" onClick={() => setShowMultiAddModal(true)}>Add Multiple Items</Button>

      <table className="table mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Status</th>
            <th>Date of Purchase</th>
            <th>Remarks</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map(item => (
            <tr key={item._id}>
              <td><Link to={`/inventory/${item._id}`}>{item.name}</Link></td>
              <td>{item.name}</td>
              <td>{item.status}</td>
              <td>{item.dateOfPurchase ? new Date(item.dateOfPurchase).toLocaleDateString() : ''}</td>
              <td className="fst-italic">{item.remarks}</td>
              <td>
                <Button variant="warning" size="sm" onClick={() => handleShowEditModal(item)}>Edit</Button>{' '}
                <Button variant="danger" size="sm" onClick={() => handleDeleteClick(item)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Item' : 'Add Item'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>ID</Form.Label>
              <Form.Control
                type="number"
                value={currentItem.id}
                onChange={(e) => setCurrentItem({ ...currentItem, id: parseInt(e.target.value) })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={currentItem.name}
                onChange={(e) => setCurrentItem({ ...currentItem, name: e.target.value })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={currentItem.status}
                onChange={(e) => setCurrentItem({ ...currentItem, status: e.target.value })}
              >
                <option>Stock In</option>
                <option>Issued</option>
                <option>Lost</option>
                <option>Repaired</option>
                <option>Damaged</option>
              </Form.Select>
            </Form.Group>

            <Form.Group>
              <Form.Label>Date of Purchase</Form.Label>
              <Form.Control
                type="date"
                value={currentItem.dateOfPurchase}
                onChange={(e) => setCurrentItem({ ...currentItem, dateOfPurchase: e.target.value })}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>Remarks</Form.Label>
              <Form.Control
                type="text"
                value={currentItem.remarks}
                onChange={(e) => setCurrentItem({ ...currentItem, remarks: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleSaveItem}>Save</Button>
        </Modal.Footer>
      </Modal>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Item</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this item?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>Cancel</Button>
          <Button variant="danger" onClick={handleDeleteItem}>Delete</Button>
        </Modal.Footer>
      </Modal>

      {/* Add Multi Items Modal */}
      <Modal show={showMultiAddModal} onHide={() => setShowMultiAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Multiple Items</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Enter multiple items (one per line, semicolon-separated: id;name;status;dateOfPurchase;remarks)</Form.Label>
              <Form.Control as="textarea" rows={10} value={multiInput} onChange={(e) => setMultiInput(e.target.value)} />
            </Form.Group>
            <Button variant="primary" className="mt-2" onClick={handleMultiImport}>Import Items</Button>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowMultiAddModal(false)}>Close</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Inventory;
